package learningjavabasics;

public class Learningoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 10; //4 bytes
		byte kid = 126; //2 bytes
		
		boolean val = true;
		boolean negVal = false;
		
		System.out.println("boolean value: "+val);
		System.out.println("boolean negative value: "+negVal);
		
		System.out.println("The Age value in the integer variable is: "+age);
		System.out.println("Kid value in the byte variable is: "+kid);
		
		short babies = 3;
		long bigNumber = 787879789;
		
		System.out.println("The short value in the babies variable is: " + babies);
		System.out.println("The bignumber value in the bigNumber variable is: " + bigNumber);
		
		float flotnum = 15.5f;
		double doublenum = 20.2d;
		
		System.out.println("The float number value in the flotnum variable is: " + flotnum);
		System.out.println("The double number value in the doublenum variable is: " + doublenum);
		
		String name = "Anselm";
		
		System.out.println("The name value in the name variable is: " + name);
		
		char character = 'A';
		
		System.out.println("The charcter value in the character variable is: " + character);
	}

}
